CREATE TABLE DEADLINE (id NUMBER NOT NULL, DEADLINE_DATE TIMESTAMP NOT NULL, NAME VARCHAR2(255) NOT NULL, STATUS_TOBE_ACHIEVED VARCHAR2(255) NOT NULL, TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE EVENT (id NUMBER NOT NULL, DETAILS VARCHAR2(255), NEW_STATE VARCHAR2(255), OLD_STATE VARCHAR2(255), TIMESTAMP TIMESTAMP NOT NULL, TYPE VARCHAR2(255) NOT NULL, USER VARCHAR2(255) NOT NULL, TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE GENERIC_HUMAN_ROLE (GHR_ID NUMBER NOT NULL, type VARCHAR2(23), TASK_ID NUMBER, PRIMARY KEY (GHR_ID));
CREATE TABLE HTJOB (id NUMBER NOT NULL, DETAILS VARCHAR2(4096), NAME VARCHAR2(255), NODEID VARCHAR2(255), SCHEDULED NUMBER NOT NULL, TASKID NUMBER NOT NULL, TIME NUMBER NOT NULL, TRANSACTED NUMBER NOT NULL, TYPE VARCHAR2(255) NOT NULL, PRIMARY KEY (id));
CREATE TABLE HUMANROLE_ORGENTITY (HUMANROLE_ID NUMBER, ORGENTITY_ID NUMBER);
CREATE TABLE MESSAGE (MESSAGE_ID NUMBER NOT NULL, DATA CLOB, HEADER CLOB, MESSAGE_TYPE VARCHAR2(255), NAME VARCHAR2(512), TASK_ID NUMBER, PRIMARY KEY (MESSAGE_ID));
CREATE TABLE OPENJPA_SEQUENCE_TABLE (ID NUMBER NOT NULL, SEQUENCE_VALUE NUMBER, PRIMARY KEY (ID));
CREATE TABLE ORG_ENTITY (ORG_ENTITY_ID NUMBER NOT NULL, ORG_ENTITY_NAME VARCHAR2(255), ORG_ENTITY_TYPE VARCHAR2(255), PRIMARY KEY (ORG_ENTITY_ID));
CREATE TABLE PRESENTATION_ELEMENT (id NUMBER NOT NULL, CONTENT VARCHAR2(255), XML_LANG VARCHAR2(255), PE_TYPE VARCHAR2(31), CONTENT_TYPE VARCHAR2(255), TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE PRESENTATION_PARAM (id NUMBER NOT NULL, NAME VARCHAR2(255), type VARCHAR2(20), VALUE VARCHAR2(255), TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE TASK (id NUMBER NOT NULL, ACTIVATION_TIME TIMESTAMP, COMPLETE_BY_TIME TIMESTAMP, CREATED_ON TIMESTAMP, ESCALATED NUMBER, EXPIRATION_TIME TIMESTAMP, NAME VARCHAR2(255) NOT NULL, PRIORITY NUMBER NOT NULL, SKIPABLE NUMBER, START_BY_TIME TIMESTAMP, STATUS VARCHAR2(255) NOT NULL, STATUS_BEFORE_SUSPENSION VARCHAR2(255), TENANT_ID NUMBER NOT NULL, TYPE VARCHAR2(255) NOT NULL, UPDATED_ON TIMESTAMP, FAILURE_MESSAGE NUMBER, INPUT_MESSAGE NUMBER, OUTPUT_MESSAGE NUMBER, PARENTTASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE TASK_ATTACHMENT (id NUMBER NOT NULL, ACCESS_TYPE VARCHAR2(255), ATTACHED_AT TIMESTAMP, CONTENT_TYPE VARCHAR2(255), ATTACHMENT_NAME VARCHAR2(255), VALUE VARCHAR2(255), TASK_ID NUMBER, ATTACHED_BY NUMBER, PRIMARY KEY (id));
CREATE TABLE TASK_COMMENT (id NUMBER NOT NULL, COMMENT_TEXT VARCHAR2(8192), COMMENTED_BY VARCHAR2(100), COMMENTED_ON TIMESTAMP, MODIFIED_BY VARCHAR2(100), MODIFIED_ON TIMESTAMP, TASK_ID NUMBER, PRIMARY KEY (id));
CREATE INDEX I_DEDLINE_TASK ON DEADLINE (TASK_ID);
CREATE INDEX I_EVENT_TASK ON EVENT (TASK_ID);
CREATE INDEX I_GNRC_RL_TASK ON GENERIC_HUMAN_ROLE (TASK_ID);
CREATE INDEX I_HMNRTTY_ELEMENT ON HUMANROLE_ORGENTITY (ORGENTITY_ID);
CREATE INDEX I_HMNRTTY_HUMANROLE_ID ON HUMANROLE_ORGENTITY (HUMANROLE_ID);
CREATE INDEX I_MESSAGE_TASK ON MESSAGE (TASK_ID);
CREATE INDEX I_PRSNMNT_DTYPE ON PRESENTATION_ELEMENT (PE_TYPE);
CREATE INDEX I_PRSNMNT_TASK ON PRESENTATION_ELEMENT (TASK_ID);
CREATE INDEX I_PRSNPRM_TASK ON PRESENTATION_PARAM (TASK_ID);
CREATE INDEX I_TASK_FAILUREMESSAGE ON TASK (FAILURE_MESSAGE);
CREATE INDEX I_TASK_INPUTMESSAGE ON TASK (INPUT_MESSAGE);
CREATE INDEX I_TASK_OUTPUTMESSAGE ON TASK (OUTPUT_MESSAGE);
CREATE INDEX I_TASK_PARENTTASK ON TASK (PARENTTASK_ID);
CREATE INDEX I_TSK_MNT_ATTACHEDBY ON TASK_ATTACHMENT (ATTACHED_BY);
CREATE INDEX I_TSK_MNT_TASK ON TASK_ATTACHMENT (TASK_ID);
CREATE INDEX I_TSK_MNT_TASK1 ON TASK_COMMENT (TASK_ID);
