package samples.greeting;

import java.util.Calendar;

/**
 * User
 */
public class User {

    private String name ;

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public int now(){
        Calendar cal = Calendar.getInstance();
        return cal.get(Calendar.HOUR_OF_DAY);
    }
}

package samples.greeting;

/**
 * greeting message
 */
public class GreetingMessage {

    private String message ;

    public String getMessage() {
        return message;
    }

    public void setMessage(String message) {
        this.message = message;
    }
}

package greeting

import samples.greeting.GreetingMessage;
import samples.greeting.User;

rule "Is Morning" no-loop true
when
    user : User()
    eval((6 < user.now()) && (user.now()< 12))
then
    GreetingMessage msg = new GreetingMessage();
    msg.setMessage("Good Morning  " + user.getName() + " !!! ");
    insertLogical(msg);
end

rule "Is afternoon" no-loop true
when
    user : User()
    eval((12 <= user.now()) && (user.now() < 18))
then
    GreetingMessage msg = new GreetingMessage();
    msg.setMessage("Good Afternoon  " + user.getName() + " !!! ");
    insertLogical(msg);
end

rule "Is Night" no-loop true
when
    user : User()
    eval( (18 <= user.now()) && (user.now() < 24))
then
    GreetingMessage msg = new GreetingMessage();
    msg.setMessage("Good Night  " + user.getName() + " !!! ");
    insertLogical(msg);
end

import org.apache.axis2.AxisFault;
import org.wso2.carbon.brs.GreetMeRequest;
import org.wso2.carbon.brs.GreetMeResponse;
import org.wso2.carbon.brs.GreetingServiceStub;
import org.wso2.carbon.brs.Result;
import samples.greeting.xsd.GreetingMessage;
import samples.greeting.xsd.User;

import java.rmi.RemoteException;

/**
 * A client for testing  GreetingService
 */
public class TestGreetingService {

    public static void main(String arg[]) {

        try {

            GreetingServiceStub greetingServiceStub =
                    new GreetingServiceStub("http://localhost:9763/services/GreetingService");

            GreetMeRequest greetMeRequest = new GreetMeRequest();
            User user = new User();
            user.setName("your name");
            greetMeRequest.setUser(user);

            GreetMeResponse response =
                    greetingServiceStub.greetMe(greetMeRequest);
            Result result = response.getResult();
            GreetingMessage greetingMessage = result.getGreetingMessage();
            if (greetingMessage != null) {
                System.out.print(greetingMessage.getMessage());
            }

        } catch (AxisFault axisFault) {
            axisFault.printStackTrace();
        } catch (RemoteException e) {
            e.printStackTrace();
        }
    }

}
