head.script:
//
function windowTitle()
{
    parent.document.title="org.wso2.mashup.hostobjects.feed (WSO2 Mashup Server Host Objects API)";
}
//

tr.td:
This module allows the addition of Media Content to an Entry
ex: //Creating a Feed var mashupFeed = new Feed();
mashupFeed.feedType = "rss_2.0"; mashupFeed.title =
"EarthShots.org"; mashupFeed.description = "Photos from
EarthShots.org"; mashupFeed.link = "http://www.earthshots.org";
//Creating an Entry for the Feed var newEntry = new Entry();
newEntry.title = "Capitol Peak Evening by Tad Bowman";
newEntry.link =
"http://www.earthshots.org/2007/10/capitol-peak-evening-by-tad-bowman/";
newEntry.description = "<a
href="http://www.earthshots.org/2007/10/capitol-peak-evening-by-tad-bowman/"><img
src="http://www.earthshots.org/photos/387.thumb.jpg" alt="Capitol
Peak Evening by Tad Bowman" /></a>"; //Creating a Media
module to be added to the Entry var mediaModule = new
MediaModule("http://www.earthshots.org/photos/387.jpg");
mediaModule.copyright = "2007 Tad Bowman"; mediaModule.type =
"image/jpeg"; mediaModule.thumbnail =
"http://www.earthshots.org/photos/387.thumb.jpg"; //Adding the
Media Module to the Entry newEntry.addMediaModule(mediaModule);
//Adding the Entry to the Feed
mashupFeed.insertEntry(newEntry);
